import React, {useState}from 'react';

export default function Textform() {

    const [mytext, setText] = useState('');



    const handleTextChange = (event) => {
        setText(event.target.value);
      };

    const convertToUpperCase = () => {
        let newtext= mytext.toUpperCase()
        setText(newtext)
        document.getElementById("buttonText").innerText = newtext;

        };

  return (
    <div>
    <div className="container">
        <div class="mb-3">
        </div>
        <div class="mb-3">

        <label for="exampleFormControlTextarea1" class="form-label"><h2>Enter you text below</h2></label>

        {/* <textarea class="form-control" id="textArea" rows="9" onChange={handleTextChange} value={mytext}></textarea> */}
        <textarea class="form-control" id="textArea" rows="9" onChange={handleTextChange}></textarea>
        </div>
        <div>
            <button class="btn btn-primary" type="submit" onClick={convertToUpperCase}>Convert to uppercase</button>
        </div>

        <div>
        <textarea class="form-control" id="buttonText" rows="9"></textarea>
        </div>
        <div><p>Your text summary: {mytext.length} words, {mytext.split(" ").length} words and {} charact</p></div>
    </div>
</div>
  )
}
