import './App.css';
import Navbar from './components/Navbar';
import About from './components/About';
// import Textform from './components/Textform';
import React, { useState } from 'react';
import Switch,{} from './components/Switch';



function App() {
  const [mode, setTo]=useState("light")

  const modeSwitch=() => {
    if (mode==='light'){
      setTo('dark')
    }
    else if (mode==='dark'){
      setTo('light')
    };
}
  return (
    <>
        {/* <Navbar title="textutils" mode={mode} about="About us"/> */}
        {/* <About/> */}
        {/* <Textform/> */}

        <div class="form-check form-switch">
            <input class="form-check-input" type="checkbox" onChange={modeSwitch} role="switch" id="flexSwitchCheckDefault"/>
            <label class="form-check-label" for="flexSwitchCheckDefault">Enable Dark mode.</label>
        </div>
    <Switch mode="mode" modeSwitch={modeSwitch}/>
    </>
  )}

export default App;
