import React from 'react'

export default function Switch(props) {
    if (props.mode==='light'){
        document.body.style.backgroundColor = 'white';
        document.body.style.color = 'black';

      }
      else if (props.mode==='dark'){
        document.body.style.backgroundColor = '#2f4f4f';
        document.body.style.color = 'white';

      };
      // if (props.alert!=null){
      //   const newParagraph = document.createElement('p');
      //   newParagraph.textContent = props.alert;
      //   const parentDiv = document.getElementById('alertID');
      //   // parentDiv.innerHTML = '';
      //   parentDiv.appendChild(newParagraph);
      // }
  return (
    <div>
      <div class="form-check form-switch">
            <input class="form-check-input" type="checkbox" onChange={(event) => {
        props.modeSwitch(event);props.alert(event);
        }} role="switch" id="flexSwitchCheckDefault"/>
            <label class="form-check-label" for="flexSwitchCheckDefault">Enable Dark mode.</label>
        </div>
        <div class="alert alert-warning" role="alert" id="alertID">
        {props.alert}
        </div>

    </div>
  )
}
