import About from './components/About';
import DarkMode from './components/DarkMode';

function App() {

  const getStyles = (color, backgroundColor) =>
    {  return {color,backgroundColor } };


    const [styles, setStyle] = useState(getStyles('black','white'))

    const modeSwitch = () => {
        if (styles.color === 'black') {
            setStyle(getStyles('white', 'black'))


        } 
        else {
            setStyle(getStyles('black', 'white'));
        }
        }

  return (
    <>
        {/* <Navbar title="textutils" about="About us"/> */}
        {/* <Textform/> */}
        <About/>
        <DarkMode styles={styles} modeSwitch={modeSwitch}/>


    </>
  )}

export default App;
