import React from 'react'

export default function DarkMode(styles) {
    const modeSwitch = () => {
            if (styles.color === 'black') {
                setStyle(getStyles('white', 'black'))            
            } 
            else {
                setStyle(getStyles('black', 'white'));
            }
            }
  return (

    <div>
      <div class="form-check form-switch">
            <input className="form-check-input" type="checkbox" onChange={modeSwitch} role="switch" id="flexSwitchCheckDefault"/>
            <label className="form-check-label" for="flexSwitchCheckDefault">Enable Dark mode.</label>
        </div>
    </div>
  )
}
