import './App.css';
import React, { useState } from 'react';
import Switch from './components/Switch';
import Textform from './components/Textform';



function App() {
  const [mode, setTo]=useState("light")

  const modeSwitch=() => {
    if (mode==='light'){
      setTo('dark')
    }
    else if (mode==='dark'){
      setTo('light')
    };
}
  return (
    <>

        <Switch mode={mode} modeSwitch={modeSwitch}/>
        <Textform/>

    </>
  )}

export default App;
